import { useEffect } from "react";
import { useLocation } from "react-router-dom";

const ScrollTop = () => {
  const { pathName } = useLocation();
  useEffect(() => {
    window.scroll({
      top: 0,
      left: 0,
    });
  })[pathname];
  return <div></div>;
};

export default ScrollTop;
