import styled from "styled-components";
import { motion } from "framer-motion";

export const About = styled(motion.div)`
  min-height: 90vh;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 5rem 10rem;
  color: #edf5e0;
  @media (max-width: 1300px) {
    display: block;
    padding: 2rem 2rem;
  }
`;

export const Description = styled.div`
  flex: 1;
  padding-right: 9rem;
  font-weight: lighter;
  z-index: 2;
  @media (max-width: 1300px) {
    padding: 0;
    button: {
      margin: 2rem 0rem 5 rem 0rem;
    }
  }
`;

export const Image = styled.div`
  flex: 1;
  overflow: hidden;
  z-index: 2;

  img {
    width: 100%;
    height: 80vh;
    object-fit: cover;
  }
`;

export const Hide = styled.div`
  overflow: hidden;
`;
